import './App.css';
import React, { Component }  from 'react';
import { ProductoService } from './service/productoService';
import {NuevoService} from './service/nuevoService'
import {DataTable} from 'primereact/datatable'
import { Column } from 'primereact/column';
import { Panel } from 'primereact/panel';
import { Menubar } from 'primereact/menubar';
import { Button } from 'primereact/button';

import'primereact/resources/themes/nova-alt/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';


export default class App extends Component{
  constructor(){
    super();
    this.items=[
      {
        label: "precio(clp)",
        icon: 'pi pi-dollar',
        command: ()=>{this.getPrecios()}

      },
      {
        label: "precio(usd-eur)",
        icon: 'pi pi-dollar',
        command: ()=>{this.getPreciosED()}

      },
      {
        label: "actualizar",
        icon: 'pi pi-refresh',
        command: ()=>{this.actualizar()}
      }
    ];
    this.state={};
    this.productosService=new ProductoService();
    this.precioUsd=new NuevoService();

  }

  componentDidMount(){


  }

  getPrecios(){
    this.productosService.getAll().then(data=>this.setState({productos: data}));
  }

  getPreciosED(){
    this.precioUsd.getAll().then(data=>this.setState({productos:data}));
  }

  actualizar(){
    this.precioUsd.actualizar().then(data=>this.setState({productos:data}) );
  }


  render(){
    return (
      <div>
        <Button label = "precio clp " onClick={this.getPrecios}/>
        <Menubar model ={this.items}/>
        <Panel header ="lista de precios">
      <DataTable value={this.state.productos}>
        <Column field='id' header="ID"></Column>
        <Column field='nombre' header="Nombre"></Column>
        <Column field='precio' header="Precio"></Column>
        <Column field='usd' header=" Precio usd"></Column>
        <Column field='eur' header="Precio eur"></Column>
      </DataTable>
      </Panel>
      </div>

    )
  }

}
